import React from 'react';
import styled from 'styled-components';
import { Card, CardContent, CardMedia, Typography, Chip } from '@mui/material';
import LocationOnIcon from '@mui/icons-material/LocationOn';
import Rating from '../UI/Rating';

const StyledCard = styled(Card)`
  width: 100%;
  border-radius: 16px !important;
  overflow: hidden;
  transition: transform 0.3s ease, box-shadow 0.3s ease;
  
  &:hover {
    transform: translateY(-5px);
    box-shadow: 0 12px 20px rgba(0, 0, 0, 0.1) !important;
  }
`;

const RestaurantImage = styled(CardMedia)`
  height: 200px;
  object-fit: cover;
`;

const ContentContainer = styled(CardContent)`
  padding: 16px !important;
`;

const Header = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: flex-start;
  margin-bottom: 12px;
`;

const Title = styled(Typography)`
  font-weight: 600 !important;
  margin-bottom: 4px !important;
`;

const Categories = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 8px;
  margin-top: 12px;
`;

const StyledChip = styled(Chip)`
  font-size: 0.75rem !important;
  height: 24px !important;
`;

const Location = styled.div`
  display: flex;
  align-items: center;
  gap: 4px;
  margin-top: 8px;
  color: #666;
`;

const RestaurantCard = ({ restaurant, onRatingChange }) => {
  const { id, name, rating, image, address, categories, priceLevel, cuisineType } = restaurant;

  const handleRatingChange = (event, newValue) => {
    onRatingChange(id, newValue);
  };

  // Format price level as $ symbols
  const formatPriceLevel = (level) => {
    return '$'.repeat(level);
  };

  return (
    <StyledCard elevation={3}>
      <RestaurantImage
        image={image || 'https://via.placeholder.com/400x200?text=Restaurant'}
        title={name}
        alt={name}
      />
      
      <ContentContainer>
        <Header>
          <div>
            <Title variant="h6">{name}</Title>
            <Typography variant="body2" color="textSecondary">
              {cuisineType} â€¢ {formatPriceLevel(priceLevel)}
            </Typography>
          </div>
          
          <Rating 
            value={rating} 
            onChange={handleRatingChange} 
            size="small"
          />
        </Header>
        
        <Location>
          <LocationOnIcon fontSize="small" />
          <Typography variant="body2">{address}</Typography>
        </Location>
        
        <Categories>
          {categories.map((category, index) => (
            <StyledChip key={index} label={category} size="small" />
          ))}
        </Categories>
      </ContentContainer>
    </StyledCard>
  );
};

export default RestaurantCard;
