  },
  {
    id: 6,
    name: "Taco Fiesta",
    rating: 4.3,
    image: "https://images.unsplash.com/photo-1551504734-5ee1c4a1479b?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1470&q=80",
    address: "67 Sunset Blvd, West Side",
    categories: ["Mexican", "Tacos", "Margaritas"],
    priceLevel: 2,
    cuisineType: "Mexican"
  }
];

// Mock delay to simulate API request
const delay = (ms) => new Promise(resolve => setTimeout(resolve, ms));

// Mock API functions
export const fetchRestaurants = async () => {
  // Simulate network delay
  await delay(800);
  return [...restaurants];
};

export const updateRestaurantRating = async (restaurantId, newRating) => {
  // Simulate network delay
  await delay(500);
  
  // Find restaurant and update rating
  const restaurant = restaurants.find(r => r.id === restaurantId);
  if (restaurant) {
    restaurant.rating = newRating;
    return { success: true, restaurant };
  }
  
  return { success: false, error: "Restaurant not found" };
};
